import React, { useContext, useEffect, useState } from "react";
import { AuthContext } from "../context/AuthContext";

export default function Home() {
  const { user, api } = useContext(AuthContext);
  const [tasks, setTasks] = useState([]);
  const [text, setText] = useState("");
  const [editingId, setEditingId] = useState(null);
  const [editingText, setEditingText] = useState("");
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // fetch tasks
  const fetchTasks = async () => {
    try {
      setLoading(true);
      const res = await api.get("/tasks"); // proxied to backend
      setTasks(res.data || []);
    } catch (err) {
      setError(err?.response?.data?.message || "Could not load tasks");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchTasks();
  }, []);

  // create
  const addTask = async (e) => {
    e?.preventDefault();
    if (!text.trim()) return;
    try {
      const res = await api.post("/tasks", { content: text.trim() });
      setTasks((s) => [res.data, ...s]);
      setText("");
    } catch (err) {
      alert(err?.response?.data?.message || "Add task failed");
    }
  };

  // toggle complete
  const toggleComplete = async (task) => {
    try {
      const res = await api.put(`/tasks/${task._id}`, {
        content: task.content,
        completed: !task.completed,
      });
      setTasks((s) => s.map((t) => (t._id === task._id ? res.data : t)));
    } catch (err) {
      alert("Update failed");
    }
  };

  // start editing
  const startEdit = (task) => {
    setEditingId(task._id);
    setEditingText(task.content);
  };

  // save edit
  const saveEdit = async (taskId) => {
    if (!editingText.trim()) return;
    try {
      const res = await api.put(`/tasks/${taskId}`, {
        content: editingText.trim(),
        completed: tasks.find((t) => t._id === taskId)?.completed || false,
      });
      setTasks((s) => s.map((t) => (t._id === taskId ? res.data : t)));
      setEditingId(null);
      setEditingText("");
    } catch (err) {
      alert("Save failed");
    }
  };

  // delete
  const deleteTask = async (taskId) => {
    if (!window.confirm("Delete this task?")) return;
    try {
      await api.delete(`/tasks/${taskId}`);
      setTasks((s) => s.filter((t) => t._id !== taskId));
    } catch (err) {
      alert("Delete failed");
    }
  };

  return (
    <div className="app">
      <header className="header">
        <div className="header-left">
          <h1>Welcome to Your To-Do List</h1>
          <div className="subtitle">{user ? `Hi, ${user.username}` : "Your Tasks"}</div>
        </div>
      </header>

      <main className="main" style={{ gridTemplateColumns: "1fr" }}>
        <section className="tasks">
          <div className="card">
            <form className="task-form" onSubmit={addTask}>
              <input
                type="text"
                placeholder="Add a task..."
                value={text}
                onChange={(e) => setText(e.target.value)}
              />
              <button type="submit">Add</button>
            </form>
          </div>

          {loading && <div className="card">Loading tasks…</div>}
          {error && <div className="card" style={{ color: "crimson" }}>{error}</div>}

          {!loading && tasks.length === 0 && (
            <div className="card empty">No tasks yet — add one!</div>
          )}

          {tasks.map((task) => (
            <div key={task._id} className={`card task-item ${task.completed ? "completed" : ""}`}>
              <div style={{ display: "flex", gap: 12, alignItems: "center" }}>
                <input
                  type="checkbox"
                  checked={task.completed}
                  onChange={() => toggleComplete(task)}
                />
                {editingId === task._id ? (
                  <input
                    style={{ minWidth: 200 }}
                    value={editingText}
                    onChange={(e) => setEditingText(e.target.value)}
                  />
                ) : (
                  <div style={{ display: "flex", flexDirection: "column" }}>
                    <span className="task-title">{task.content}</span>
                    <small className="task-muted">
                      {new Date(task.createdAt).toLocaleString()}
                    </small>
                  </div>
                )}
              </div>

              <div style={{ display: "flex", gap: 8 }}>
                {editingId === task._id ? (
                  <>
                    <button onClick={() => saveEdit(task._id)}>Save</button>
                    <button onClick={() => { setEditingId(null); setEditingText(""); }}>Cancel</button>
                  </>
                ) : (
                  <>
                    <button onClick={() => startEdit(task)}>Edit</button>
                    <button onClick={() => deleteTask(task._id)}>Delete</button>
                  </>
                )}
              </div>
            </div>
          ))}
        </section>
      </main>
    </div>
  );
}
