import React, { useContext, useEffect, useState } from "react";
import { AuthContext } from "../context/AuthContext";

const Home = () => {
  const { user, logout, api } = useContext(AuthContext);
  const [tasks, setTasks] = useState([]);
  const [newTask, setNewTask] = useState("");
  const [error, setError] = useState("");

  useEffect(() => {
    if (!user) return;
    const loadTasks = async () => {
      try {
        const { data } = await api.get("/tasks");
        console.log("GET /api/tasks response:", data); // <<-- debug log
        setTasks(Array.isArray(data) ? data : []);
      } catch (err) {
        console.error("Failed to fetch tasks:", err);
        setError("Failed to load tasks");
      }
    };
    loadTasks();
  }, [user, api]);

  const addTask = async (e) => {
    e.preventDefault();
    if (!newTask.trim()) return;
    try {
      const { data } = await api.post("/tasks", { content: newTask });
      setTasks((prev) => [...prev, data]);
      setNewTask("");
    } catch (err) {
      console.error("Add task failed:", err);
      setError("Failed to add task");
    }
  };

  const toggleTask = async (id, completed) => {
    try {
      const { data } = await api.put(`/tasks/${id}`, { completed: !completed, content: tasks.find(t => t._id === id)?.content || "" });
      setTasks((s) => s.map((t) => (t._id === id ? data : t)));
    } catch (err) {
      console.error("Toggle failed:", err);
      setError("Failed to update task");
    }
  };

  const deleteTask = async (id) => {
    try {
      await api.delete(`/tasks/${id}`);
      setTasks((s) => s.filter((t) => t._id !== id));
    } catch (err) {
      console.error("Delete failed:", err);
      setError("Failed to delete task");
    }
  };

  if (!user) {
    return (
      <div className="container">
        <h2>Welcome to the To-Do App</h2>
        <p>Please <a href="/login">Login</a> or <a href="/register">Register</a> to continue.</p>
      </div>
    );
  }

  return (
    <div className="container">
      <h2>Hello, {user.username} 👋</h2>
      <button onClick={logout}>Logout</button>

      <form onSubmit={addTask} style={{ marginTop: "1rem" }}>
        <input
          type="text"
          value={newTask}
          onChange={(e) => setNewTask(e.target.value)}
          placeholder="Enter new task"
          required
          style={{ width: "100%", padding: ".75rem", borderRadius: 8, border: "1px solid #d1d5db" }}
        />
        <button type="submit" style={{ marginTop: 12 }}>Add Task</button>
      </form>

      {error && <p style={{ color: "red" }}>{error}</p>}

      <ul style={{ marginTop: "1rem", listStyle: "none", padding: 0 }}>
        {tasks.map((task) => (
          <li key={task._id} style={{ marginBottom: ".75rem", display: "flex", gap: "12px", alignItems: "center" }}>
            {/* Force visible color and fallback text if content missing */}
            <span style={{ color: "#111827", fontSize: "1rem", minWidth: 200 }}>
              { (task.content && task.content.length) ? task.content : (task.text || task.title || "<no content>") }
            </span>

            <small style={{ color: "#6b7280" }}>
              {task.createdAt ? new Date(task.createdAt).toLocaleString() : ""}
            </small>

            <div style={{ marginLeft: "auto", display: "flex", gap: 8 }}>
              <button onClick={() => toggleTask(task._id, !!task.completed)}>
                {task.completed ? "Undo" : "Complete"}
              </button>
              <button onClick={() => deleteTask(task._id)}>Delete</button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Home;
